import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Media } from "react-bootstrap";
import PublicNavBar from "../components/PaginationBar";
import Header from "../components/Header";
import "../App.css"

const API_KEY = "3f8af128099b08ebdb593c5711c409c3";
const API_URL = "https://api.themoviedb.org/3";

const MovieDetail = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState([]);
  let time = parseInt(movie.runtime);
  console.log("this is ", time);
  useEffect(() => {
    const fetchData = async () => {
      const url = `${API_URL}/movie/${id}?api_key=${API_KEY}`;
      const respone = await fetch(url);
      const data = await respone.json();
      setMovie(data);
      console.log(movie);
    };
    fetchData();
  }, []);
  return (
    <div>
      <PublicNavBar />
      <Header />
      <div className="mt-5">
        <Container>
          <Media style={{ backgroundColor: "white" }}>
            <img
              width={300}
              className="mr-3"
              src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
              alt="Generic placeholder"
            />
            <Media.Body className="body">
              <div className="ml-5">
              <h2>
                <span style={{ fontWeight: "bolder" }}>{movie.title}</span>
                <span>
                  ({movie.release_date && movie.release_date.substring(0, 4)})
                </span>
              </h2>
              {movie.genres && movie.genres.map((e) => <span className="mx-3 genres">{e.name}</span>)}
              <div className="mt-3">
                <span>⭐ {movie.vote_average}</span>
                <span className="ml-5">
                  {Math.floor(time / 60) + "h " + (time % 60) + "m"}
                </span>
                <button className="button">▶ Play Trailer</button>
              </div>
              <h4>Overview</h4>
              <p>{}</p>
              </div>

            </Media.Body>
          </Media>
        </Container>
      </div>
    </div>
  );
};

export default MovieDetail;
